function submitAnswers(e){e.preventDefault();for(var total=6,score=0,q1=document.forms.quizForm.q1.value,q2=document.forms.quizForm.q2.value,q3=document.forms.quizForm.q3.value,q4=document.forms.quizForm.q4.value,q5=document.forms.quizForm.q5.value,q6=document.forms.quizForm.q6.value,i=1;i<=total;i++)if(null==eval("q"+i)||""==eval("q"+i)){var warning=document.getElementById("results");return warning.style.backgroundColor="#fff000",warning.innerHTML="<h3>You missed "+i+"</h3>",!1}for(var answers=["a","b","c","a","b","c"],i=1;i<=total;i++)eval("q"+i)==answers[i-1]&&score++;var results=document.getElementById("results");results.style.backgroundColor="#1EFF1E",results.innerHTML="<h3>You scored "+score+" out of "+total+"</h3>"}function next(a){var b=a.currentTarget;b.parentElement.style.display="none",b.parentElement.nextElementSibling.style.display="block"}function previous(a){var b=a.currentTarget;b.parentElement.style.display="none",b.parentElement.previousElementSibling.style.display="block"}document.getElementById("quizForm").addEventListener("submit",submitAnswers);