function submitAnswers(e){e.preventDefault();for(var total=3,score=0,q1=document.forms.quizForm.q1.value,q2=document.forms.quizForm.q2.value,q3=document.forms.quizForm.q3.value,i=1;i<=total;i++)if(null==eval("q"+i)||""==eval("q"+i))return alert("You missed question "+i),!1;for(var answers=["a","b","c"],i=1;i<=total;i++)eval("q"+i)==answers[i-1]&&score++;var results=document.getElementById("results");results.innerHTML="<h3>You scored "+score+" out of "+total+"</h3>"}document.getElementById("quizForm").addEventListener("submit",submitAnswers);